import React, { useState } from "react";
import { parseCurrency } from "@reach-sh/stdlib/ALGO";

import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
/*import Spinner from "react-bootstrap/Spinner";*/

const VoterViews = ({ appState, args, shouldBuyTicketReady, shouldBuyTicket }) => {
    switch (appState) {
        case "shouldBuyTicket":
            return (
                shouldBuyTicketReady
                    ? <BuyTicket functionToCall={shouldBuyTicket} />
                    : <h1>Loading the shouldBuyTicket page</h1>);
        case "shouldPay":
            return (
                shouldPayReady
                    ? <BuyTicket functionToCall={shouldPay} />
                    : <h1>Loading the shouldBuyTicket page</h1>);
        case "showOutcome":
            return (<ShowOutcome address={args[0]} />);
        case "showPurchase":
            return (<ShowPurchase address={args[0]} />);
        default:
            return (<h1>Waiting for contract</h1>);
    }
}

const BuyTicket = ({ price, functionToCall }) => {

    const [index, setIndex] = useState(0);
    const [donation, setDonation] = useState(0);

    const handleClick = () => {
        functionToCall({
            index: index,
            donation: parseCurrency(donation)
        });
    }

    return (
        <Container>
            <Form.Group>
                <p>Index</p>
                <Form.Control
                    value={index}
                    onChange={(e) => setIndex(e.target.value)}
                    type="number"
                    placeholder="Deadline in blocks" />
                <p>Donation Amount</p>
                <Form.Control
                    value={donation}
                    onChange={(e) => setDonation(e.target.value)}
                    type="number"
                    placeholder="Ticket price" />
            </Form.Group>
            <Button onClick={handleClick}>Set Parameters</Button>
        </Container>
    );
}

const shouldPay = ({functionToCall}) => {
    const handleClick = () => {
        functionToCall({
            boolVal: true,
            donation: parseCurrency(donation)
        });
    }

}

const ShowOutcome = ({ address }) => {
    return (
        <Container>
            <h1>Winner address: <small>{address}</small></h1>
        </Container>
    );
}

const ShowPurchase = ({ address }) => {
    return (
        <Container>
            <h1><small>{address}</small> bought ticket</h1>
        </Container>
    );
}

export default VoterViews;
